mapper.select:
select * from t_user 
where
loginacct = #{loginacct}
and
userpswd = #{userpswd}

select * from t_user
and loginacct like concat('%',#{querytext},'%') escape '#'
limit #{start},#{size}

mapper.insert:
		insert into t_user_role (
            userid, roleid
        ) values (
            #{userid}, #{roleid}
        )

mapper.update:
		update t_user
        set
            loginacct = #{loginacct},
            username = #{username},
            email = #{email}
        where id = #{id}
